pragma solidity 0.8.16;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlEnumerableUpgradeable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import {Cosmetics} from "./modules/Cosmetics.sol";

contract MothoraGame is Initializable, AccessControlEnumerableUpgradeable {
    using Counters for Counters.Counter;

    Counters.Counter public accountsCounter;

    enum Faction {
        NONE,
        THOROKS,
        CONGLOMERATE,
        DOC
    }
    uint256[4] public totalFactionMembers;

    struct Account {
        uint256 id;
        bool frozen;
        Faction faction;
    }
    bytes32 public constant MOTHORA_GAME_MASTER = keccak256("MOTHORA_GAME_MASTER");

    address[] private accountAddresses;

    // Player address => Struct Account
    mapping(address => Account) private playerAccounts;

    // Bytes32 id => contract Address
    mapping(bytes32 => address) private gameProtocolAddresses;

    bytes32 private constant ARENA = "ARENA";
    bytes32 private constant ARTIFACTS = "ARTIFACTS";
    bytes32 private constant COSMETICS = "COSMETICS";
    bytes32 private constant CRAFTING = "CRAFTING";
    bytes32 private constant ESSENCE = "ESSENCE";
    bytes32 private constant ESSENCE_FIELD = "ESSENCE_FIELD";
    bytes32 private constant ESSENCE_ABSORBER = "ESSENCE_ABSORBER";

    event AccountCreated(address indexed player, uint256 id);
    event AccountStatusChanged(address indexed player, bool freezeStatus);
    event ArenaModuleUpdated(address indexed arenaModule);
    event EssenceFieldUpdated(address indexed essenceField);
    event EssenceAbsorberUpdated(address indexed essenceAbsorber);
    event EssenceUpdated(address indexed essence);
    event CraftingModuleUpdated(address indexed craftingModule);
    event CosmeticsModuleUpdated(address indexed cosmetics);
    event ArtifactsModuleUpdated(address indexed artifacts);

    modifier activeAccounts() {
        uint256 id = getPlayerId(msg.sender);
        bool frozen = getPlayerStatus(msg.sender);
        require(id != 0 && !frozen, "ACCOUNT_NOT_ACTIVE");
        _;
    }

    function init() external initializer {
        _setRoleAdmin(MOTHORA_GAME_MASTER, MOTHORA_GAME_MASTER);
        _grantRole(MOTHORA_GAME_MASTER, msg.sender);
        __AccessControlEnumerable_init();
    }

    /**
     * @dev Creates an account for a player
     * @param faction The selected faction id
     **/
    function createAccount(uint256 faction) external {
        _joinFaction(faction);

        // currently using a contract id system
        // would be changed to Unreal Engine id system
        accountsCounter.increment();

        uint256 playerId = accountsCounter.current();
        playerAccounts[msg.sender].id = playerId;
        accountAddresses.push(msg.sender);

        _mintCharacterCosmeticSkin();

        emit AccountCreated(msg.sender, playerId);
    }

    /**
     * @dev Freezes an account for a player
     * @param player The address of the player whose account is being frozen
     * @param freezeStatus Whether to freeze or unfreeze the account
     **/
    function changeFreezeStatus(address player, bool freezeStatus) public onlyRole(MOTHORA_GAME_MASTER) {
        require(playerAccounts[player].id != 0, "ACCOUNT_DOES_NOT_EXIST");

        playerAccounts[player].frozen = freezeStatus;
        emit AccountStatusChanged(player, freezeStatus);
    }

    function defect(uint256 newFaction) external activeAccounts {
        require(newFaction == 1 || newFaction == 2 || newFaction == 3, "INVALID_FACTION_SELECTED");
        uint256 currentfaction = getPlayerFaction(msg.sender);
        require(newFaction != currentfaction, "CANNOT_DEFECT_TO_SAME_FACTION");

        totalFactionMembers[currentfaction] -= 1;

        if (newFaction == 1 && currentfaction != 1) {
            playerAccounts[msg.sender].faction = Faction.THOROKS;
            totalFactionMembers[1] += 1;
        } else if (newFaction == 2 && currentfaction != 2) {
            playerAccounts[msg.sender].faction = Faction.CONGLOMERATE;
            totalFactionMembers[2] += 1;
        } else if (newFaction == 3 && currentfaction != 3) {
            playerAccounts[msg.sender].faction = Faction.DOC;
            totalFactionMembers[3] += 1;
        }
        // TODO restake or unstake Artifacts according to what the player desires
    }

    /**
     * @dev Returns a player's id
     * @return Players'id
     */
    function getPlayerId(address player) public view returns (uint256) {
        return (playerAccounts[player].id);
    }

    /**
     * @dev Returns a player's faction
     * @return Faction code
     */
    function getPlayerFaction(address player) public view returns (uint256) {
        return (uint256(playerAccounts[player].faction));
    }

    /**
     * @dev Returns a player's status
     * @return Frozen status
     */
    function getPlayerStatus(address player) public view returns (bool) {
        return playerAccounts[player].frozen;
    }

    /**
     * @dev Returns all active players
     * @return Frozen status
     */
    function getAllActivePlayers() public view returns (address[] memory) {
        return accountAddresses;
    }

    /**
     * @dev Returns an address by id
     * @return The address
     */
    function getAddress(bytes32 id) public view returns (address) {
        return gameProtocolAddresses[id];
    }

    /**
     * @dev Returns the address of the ARENA
     * @return The ARENA address
     **/
    function getArena() public view returns (address) {
        return getAddress(ARENA);
    }

    /**
     * @dev Updates the address of the ARENA
     * @param arenaModule The new ARENA address
     **/
    function setArena(address arenaModule) external onlyRole(MOTHORA_GAME_MASTER) {
        gameProtocolAddresses[ARENA] = arenaModule;
        emit ArenaModuleUpdated(arenaModule);
    }

    /**
     * @dev Returns the address of the ESSENCE_FIELD
     * @return The ESSENCE_FIELD address
     **/
    function getEssenceField() public view returns (address) {
        return getAddress(ESSENCE_FIELD);
    }

    /**
     * @dev Updates the address of the ESSENCE_FIELD
     * @param essenceField The new ESSENCE_FIELD address
     **/
    function setEssenceField(address essenceField) external onlyRole(MOTHORA_GAME_MASTER) {
        gameProtocolAddresses[ESSENCE_FIELD] = essenceField;
        emit EssenceFieldUpdated(essenceField);
    }

    /**
     * @dev Returns the address of the ESSENCE_ABSORBER
     * @return The ESSENCE_ABSORBER address
     **/
    function getEssenceAbsorber() public view returns (address) {
        return getAddress(ESSENCE_ABSORBER);
    }

    /**
     * @dev Updates the address of the ESSENCE_ABSORBER
     * @param essenceAbsorber The new ESSENCE_ABSORBER address
     **/
    function setEssenceAbsorber(address essenceAbsorber) external onlyRole(MOTHORA_GAME_MASTER) {
        gameProtocolAddresses[ESSENCE_ABSORBER] = essenceAbsorber;
        emit EssenceAbsorberUpdated(essenceAbsorber);
    }

    /**
     * @dev Returns the address of the ESSENCE
     * @return The ESSENCE address
     **/
    function getEssence() public view returns (address) {
        return getAddress(ESSENCE);
    }

    /**
     * @dev Updates the address of the ESSENCE
     * @param essence The new ESSENCE address
     **/
    function setEssence(address essence) external onlyRole(MOTHORA_GAME_MASTER) {
        gameProtocolAddresses[ESSENCE] = essence;
        emit EssenceUpdated(essence);
    }

    /**
     * @dev Returns the address of the CRAFTING
     * @return The CRAFTING address
     **/
    function getCrafting() public view returns (address) {
        return getAddress(CRAFTING);
    }

    /**
     * @dev Updates the address of the CRAFTING
     * @param crafting The new CRAFTING address
     **/
    function setCrafting(address crafting) external onlyRole(MOTHORA_GAME_MASTER) {
        gameProtocolAddresses[CRAFTING] = crafting;
        emit CraftingModuleUpdated(crafting);
    }

    /**
     * @dev Returns the address of the COSMETICS
     * @return The COSMETICS address
     **/
    function getCosmetics() public view returns (address) {
        return getAddress(COSMETICS);
    }

    /**
     * @dev Updates the address of the COSMETICS
     * @param cosmetics The new COSMETICS address
     **/
    function setCosmetics(address cosmetics) external onlyRole(MOTHORA_GAME_MASTER) {
        gameProtocolAddresses[COSMETICS] = cosmetics;
        emit CosmeticsModuleUpdated(cosmetics);
    }

    /**
     * @dev Returns the address of the ARTIFACTS
     * @return The ARTIFACTS address
     **/
    function getArtifacts() public view returns (address) {
        return getAddress(ARTIFACTS);
    }

    /**
     * @dev Updates the address of the ARTIFACTS
     * @param artifacts The new ARTIFACTS address
     **/
    function setArtifacts(address artifacts) external onlyRole(MOTHORA_GAME_MASTER) {
        gameProtocolAddresses[ARTIFACTS] = artifacts;
        emit ArtifactsModuleUpdated(artifacts);
    }

    function unsafeInc(uint256 x) private pure returns (uint256) {
        unchecked {
            return x + 1;
        }
    }

    /**
     * @dev Assigns a faction id to an account
     **/
    function _joinFaction(uint256 faction) internal {
        require(playerAccounts[msg.sender].faction == Faction.NONE, "PLAYER_ALREADY_HAS_FACTION");
        require(faction == 1 || faction == 2 || faction == 3, "INVALID_FACTION");
        if (faction == 1) {
            playerAccounts[msg.sender].faction = Faction.THOROKS;
            totalFactionMembers[1] += 1;
        } else if (faction == 2) {
            playerAccounts[msg.sender].faction = Faction.CONGLOMERATE;
            totalFactionMembers[2] += 1;
        } else if (faction == 3) {
            playerAccounts[msg.sender].faction = Faction.DOC;
            totalFactionMembers[3] += 1;
        }
    }

    /**
     * @dev Mints a faction related cosmetic skin
     **/
    function _mintCharacterCosmeticSkin() internal {
        require(playerAccounts[msg.sender].faction != Faction.NONE, "PLAYER_HAS_NO_FACTION");
        uint256 faction = uint256(playerAccounts[msg.sender].faction);
        require(Cosmetics(getCosmetics()).balanceOf(msg.sender, faction) == 0, "ONLY_ONE_SKIN_PER_FACTION");
        Cosmetics(getCosmetics()).mintCharacter(msg.sender, faction);
    }
}
","keccak256":"0x57c5643db08051807118bed9829d11b96fcbe7b355be99c1915d131b7807f189","license":"MIT"},"contracts/modules/Artifacts.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.16;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import {MothoraGame} from "../MothoraGame.sol";

contract Artifacts is ERC1155, Ownable {
    event MothoraGameAddressUpdated(address indexed mothoraGameContractAddress);
    //===============Storage===============

    mapping(uint256 => string) private _uris;

    uint256 public constant ARTIFACTS = 0;

    MothoraGame mothoraGameContract;

    modifier onlyArena() {
        require(msg.sender == mothoraGameContract.getArena(), "NOT_ARENA");
        _;
    }
    modifier onlyCrafting() {
        require(msg.sender == mothoraGameContract.getCrafting(), "NOT_CRAFTING");
        _;
    }

    modifier onlyArenaOrCrafting() {
        require(
            msg.sender == mothoraGameContract.getCrafting() || msg.sender == mothoraGameContract.getArena(),
            "NOT_CRAFTING_OR_ARENA"
        );
        _;
    }

    //===============Functions=============

    // To translate CIDv0 (Qm) to CIDv1 (ba) use this website: https://cid.ipfs.io/
    // constructor() ERC1155("https://bafybeiex2io5lawckt4bgjjyhmvfy7yk72s4fmhuxj2rgehwzaa6lderkm.ipfs.dweb.link/{id}.json") {}

    constructor(string memory _initialFolder, address _mothoraGameAddress)
        ERC1155(string(abi.encodePacked(_initialFolder, "{id}.json")))
    {
        setTokenUri(ARTIFACTS, string(abi.encodePacked(_initialFolder, "0", ".json")));
        mothoraGameContract = MothoraGame(_mothoraGameAddress);
    }

    function mintArtifacts(address recipient, uint256 amount) external onlyArenaOrCrafting {
        _mint(recipient, ARTIFACTS, amount, "");
    }

    function uri(uint256 tokenId) public view override returns (string memory) {
        return (_uris[tokenId]);
    }

    function setTokenUri(uint256 tokenId, string memory NFTuri) public onlyOwner {
        require(bytes(_uris[tokenId]).length == 0, "CANNOT_SET_URI_TWICE");
        _uris[tokenId] = NFTuri;
    }

    /**
     * @dev Returns the address of the Mothora Game Hub Contract
     * @return The Mothora Game address
     **/
    function getMothoraGame() public view returns (address) {
        return address(mothoraGameContract);
    }

    /**
     * @dev Updates the address of the Mothora Game
     * @param mothoraGameContractAddress The new Mothora Game address
     **/
    function setMothoraGame(address mothoraGameContractAddress) external onlyOwner {
        mothoraGameContract = MothoraGame(mothoraGameContractAddress);
        emit MothoraGameAddressUpdated(mothoraGameContractAddress);
    }
}
","keccak256":"0x53dd1791f55dad161718e2aa3675f1ae2b45027f473a8b50d2883681128fb3c8","license":"MIT"},"contracts/modules/Cosmetics.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.16;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import {MothoraGame} from "../MothoraGame.sol";

contract Cosmetics is ERC1155, Ownable {
    event MothoraGameAddressUpdated(address indexed mothoraGameContractAddress);

    //===============Storage===============

    mapping(uint256 => string) private _uris;

    uint256 public constant THOROKS = 1;
    uint256 public constant CONGLOMERATE = 2;
    uint256 public constant DOC = 3;

    MothoraGame mothoraGameContract;

    modifier onlyMothoraGame() {
        require(msg.sender == address(mothoraGameContract), "NOT_MOTHORA_GAME");
        _;
    }

    //===============Functions=============

    // To translate CIDv0 (Qm) to CIDv1 (ba) use this website: https://cid.ipfs.io/
    // constructor() ERC1155("https://bafybeiex2io5lawckt4bgjjyhmvfy7yk72s4fmhuxj2rgehwzaa6lderkm.ipfs.dweb.link/{id}.json") {}
    // currently all characters of the same faction have the same skin

    constructor(string memory _initialFolder, address _mothoraGameAddress)
        ERC1155(string(abi.encodePacked(_initialFolder, "{id}.json")))
    {
        setTokenUri(THOROKS, string(abi.encodePacked(_initialFolder, "1", ".json")));
        setTokenUri(CONGLOMERATE, string(abi.encodePacked(_initialFolder, "2", ".json")));
        setTokenUri(DOC, string(abi.encodePacked(_initialFolder, "3", ".json")));
        mothoraGameContract = MothoraGame(_mothoraGameAddress);
    }

    function mintCharacter(address _recipient, uint256 _id) external onlyMothoraGame {
        require(_id == THOROKS || _id == CONGLOMERATE || _id == DOC, "WRONG_ID");
        _mint(_recipient, _id, 1, "");
    }

    function uri(uint256 tokenId) public view override returns (string memory) {
        return (_uris[tokenId]);
    }

    function setTokenUri(uint256 tokenId, string memory NFTuri) public onlyOwner {
        require(bytes(_uris[tokenId]).length == 0, "CANNOT_SET_URI_TWICE");
        _uris[tokenId] = NFTuri;
    }

    /**
     * @dev Returns the address of the Mothora Game Hub Contract
     * @return The Mothora Game address
     **/
    function getMothoraGame() public view returns (address) {
        return address(mothoraGameContract);
    }

    /**
     * @dev Updates the address of the Mothora Game
     * @param mothoraGameContractAddress The new Mothora Game address
     **/
    function setMothoraGame(address mothoraGameContractAddress) external onlyOwner {
        mothoraGameContract = MothoraGame(mothoraGameContractAddress);
        emit MothoraGameAddressUpdated(mothoraGameContractAddress);
    }
}
","keccak256":"0xcfd6959fe370395763869abe0ae3014799008b2156f2cd171db8dff93d85b793","license":"MIT"},"contracts/modules/EssenceAbsorber.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.16;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {ERC1155Holder} from "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {MothoraGame} from "../MothoraGame.sol";
import {Artifacts} from "./Artifacts.sol";

contract EssenceAbsorber is Ownable, ReentrancyGuard, ERC1155Holder {
    using SafeERC20 for IERC20;

    event MothoraGameAddressUpdated(address indexed mothoraGameContractAddress);

    //============== STORAGE ==============

    mapping(address => uint256) public stakedESSBalance;
    mapping(address => uint256) public RewardsBalance;
    mapping(address => uint256) public playerIds;

    mapping(address => uint256) public stakedDuration;
    mapping(address => uint256) public lastUpdate;
    mapping(address => uint256) public timeTier;

    mapping(address => uint256) public playerStakedArtifactsBalance;
    mapping(uint256 => uint256) public factionArtifactsBalance;

    MothoraGame mothoraGameContract;

    // Rewards Function variables
    uint256 public totalStakedBalance;
    uint256 public epochRewards;
    uint256 public totalArtifactsContributed;
    uint256 public lastDistributionTime;
    uint256 public epochRewardsPercentage;
    uint256 public epochDuration;
    uint256 public epochStartTime;
    // keeps a registry of all active players who staked
    address[] public playerAddresses;
    uint256 public playerId;

    //============== CONSTRUCTOR ============

    constructor(
        MothoraGame _mothoraGame,
        uint256 _epochRewardsPercentage,
        uint256 _epochDuration
    ) {
        mothoraGameContract = _mothoraGame;
        epochRewardsPercentage = _epochRewardsPercentage;
        epochDuration = _epochDuration;
        epochStartTime = block.timestamp;
    }

    modifier activeAccounts() {
        uint256 id = mothoraGameContract.getPlayerId(msg.sender);
        bool frozen = mothoraGameContract.getPlayerStatus(msg.sender);
        require(id != 0 && !frozen, "ACCOUNT_NOT_ACTIVE");
        _;
    }

    modifier existingAccounts() {
        uint256 id = mothoraGameContract.getPlayerId(msg.sender);
        require(id != 0, "ACCOUNT_DOES_NOT_EXIST");
        _;
    }

    //============== FUNCTIONS =============
    /**
     * @dev Allows active accounts only to stake tokens
     * @param amount amount to be staked
     */
    function stakeTokens(uint256 amount) public nonReentrant activeAccounts {
        require(amount > 0, "AMOUNT_NOT_HIGHER_THAN_0");
        _stakeTokens(amount);

        IERC20(mothoraGameContract.getEssence()).safeTransferFrom(msg.sender, address(this), amount);
    }

    /**
     * @dev Allows any existing account to unstake (including frozen accounts, which cannot stake anymore)
     * @param amount amount to be staked
     */
    function unstakeTokens(uint256 amount) external nonReentrant existingAccounts {
        require(amount > 0, "AMOUNT_IS_0");
        require(stakedESSBalance[msg.sender] > 0, "STAKED_BALANCE_IS_0");
        require(amount <= stakedESSBalance[msg.sender], "INVALID_UNSTAKE_OPERATION");

        stakedESSBalance[msg.sender] -= amount;
        totalStakedBalance -= amount;

        // transfer from contract to player
        IERC20(mothoraGameContract.getEssence()).safeTransfer(msg.sender, amount);
    }

    /**
     * @dev Stakes artifacts in the absorber, only active accounts can stake
     * @param amount amount to be staked
     */
    function stakeArtifacts(uint256 amount) external nonReentrant activeAccounts {
        require(amount > 0, "AMOUNT_IS_0");
        Artifacts artifactsContract = Artifacts(mothoraGameContract.getArtifacts());
        require(artifactsContract.balanceOf(msg.sender, artifactsContract.ARTIFACTS()) >= amount, "NOT_ENOUGH_BALANCE");

        playerStakedArtifactsBalance[msg.sender] += amount;
        uint256 faction = mothoraGameContract.getPlayerFaction(msg.sender);

        factionArtifactsBalance[faction] += amount;
        totalArtifactsContributed += amount;

        // Transfer from player to Staking Contract
        artifactsContract.safeTransferFrom(msg.sender, address(this), 0, amount, "");
    }

    /**
     * @dev Unstakes artifacts from the absorber. Any existing account can unstake
     * @param amount amount to be unstaked
     */
    function unstakeArtifacts(uint256 amount) external nonReentrant existingAccounts {
        require(amount > 0, "AMOUNT_IS_0");
        require(playerStakedArtifactsBalance[msg.sender] > 0, "STAKED_BALANCE_IS_0");
        require(amount <= playerStakedArtifactsBalance[msg.sender], "INVALID_UNSTAKE_OPERATION");

        playerStakedArtifactsBalance[msg.sender] -= amount;
        uint256 faction = mothoraGameContract.getPlayerFaction(msg.sender);

        factionArtifactsBalance[faction] -= amount;
        totalArtifactsContributed -= amount;

        Artifacts artifactsContract = Artifacts(mothoraGameContract.getArtifacts());
        // Transfer from contract to player
        artifactsContract.safeTransferFrom(address(this), msg.sender, 0, amount, "");
    }

    /**
     * @dev Admin function to distribute rewards after a given epoch. It operates in a push basis
     * @dev Given this operation mode it has serious limitations given the loops. A better implementation is to create a pull based system
     **/
    function distributeRewards() external onlyOwner {
        require(totalStakedBalance > 0, "NO_TOKENS_STAKED");
        uint256 lastEpochTime = epochStartTime + epochDuration * (((block.timestamp - epochStartTime) / epochDuration));
        require(lastDistributionTime < lastEpochTime, "DISTRIBUTION_ALREADY_HAPPENED");
        // total staked balance * APR percentage * 10min/1 year -> rewards in a given epoch of 10 minute
        epochRewards = divider(totalStakedBalance * epochRewardsPercentage * 600, 31536000 * 100, 0);

        address[] memory _playerAddresses = playerAddresses;
        uint256 _playerId = playerId;
        uint256 _epochRewards = epochRewards;
        uint256 maxedFactor1 = 0;
        uint256 maxedFactor2 = 0;
        uint256 maxedFactor3 = 0;
        uint256 factor1 = 0;
        uint256 factor2 = 0;
        uint256 factor3 = 0;

        for (uint256 i = 1; i <= _playerId; i = unsafeInc(i)) {
            if (stakedESSBalance[_playerAddresses[i - 1]] > 0) {
                maxedFactor1 += stakedESSBalance[_playerAddresses[i - 1]] * _calculateTimeTier(_playerAddresses[i - 1]);
            }
        }

        maxedFactor2 = totalArtifactsContributed;
        maxedFactor3 =
            mothoraGameContract.totalFactionMembers(1) *
            factionArtifactsBalance[1] +
            mothoraGameContract.totalFactionMembers(2) *
            factionArtifactsBalance[2] +
            mothoraGameContract.totalFactionMembers(3) *
            factionArtifactsBalance[3];

        if (maxedFactor2 != 0) {
            uint256 faction;
            // Distributes the rewards
            for (uint256 i = 1; i <= playerId; i = unsafeInc(i)) {
                factor1 = (stakedESSBalance[playerAddresses[i - 1]] * _calculateTimeTier(_playerAddresses[i - 1]));
                factor2 = playerStakedArtifactsBalance[_playerAddresses[i - 1]];
                faction = mothoraGameContract.getPlayerFaction(_playerAddresses[i - 1]);

                factor3 = factionArtifactsBalance[faction];

                RewardsBalance[_playerAddresses[i - 1]] +=
                    divider(factor1 * 70 * _epochRewards, maxedFactor1 * 100, 0) +
                    divider(factor2 * 25 * _epochRewards, maxedFactor2 * 100, 0) +
                    divider(factor3 * 5 * _epochRewards, maxedFactor3 * 100, 0);
            }
        } else {
            // Distributes the rewards
            for (uint256 i = 1; i <= playerId; i = unsafeInc(i)) {
                factor1 = (stakedESSBalance[playerAddresses[i - 1]] * _calculateTimeTier(playerAddresses[i - 1]));

                RewardsBalance[playerAddresses[i - 1]] += divider(factor1 * epochRewards, maxedFactor1, 0);
            }
        }
        lastDistributionTime = block.timestamp;
    }

    /**
     * @dev Claims the rewards of a given epoch. If autocompound is true it re-stakes the tokens harvested, otherwise withdraws them
     * @param autocompound true or false
     **/
    function claimEpochRewards(bool autocompound) external existingAccounts {
        uint256 transferValue = RewardsBalance[msg.sender];
        RewardsBalance[msg.sender] = 0;

        if (autocompound) {
            _stakeTokens(transferValue);
        } else {
            IERC20(mothoraGameContract.getEssence()).safeTransfer(msg.sender, transferValue);
        }
    }

    /**
     * @dev Returns the players' balances
     **/
    function getTotalBalance(address _player)
        external
        view
        returns (
            uint256 balance,
            uint256 stakedBalance,
            uint256 pendingRewards
        )
    {
        balance = IERC20(mothoraGameContract.getEssence()).balanceOf(_player);
        stakedBalance = stakedESSBalance[_player];
        pendingRewards = RewardsBalance[_player];

        return (balance, stakedBalance, pendingRewards);
    }

    /**
     * @dev Returns the current number of staked artifacts
     * @return The number of staked artifacts
     **/
    function getPlayerArtifactsBalance(address _player) external view returns (uint256) {
        return playerStakedArtifactsBalance[_player];
    }

    /**
     * @dev Returns the current number of staked artifacts per faction
     * @return The number of staked artifacts
     **/
    function getFactionArtifactsBalance(uint256 _faction) external view returns (uint256) {
        return factionArtifactsBalance[_faction];
    }

    /**
     * @dev Returns the address of the Mothora Game Hub Contract
     * @return The Mothora Game address
     **/
    function getMothoraGame() public view returns (address) {
        return address(mothoraGameContract);
    }

    /**
     * @dev Updates the address of the Mothora Game
     * @param mothoraGameContractAddress The new Mothora Game address
     **/
    function setMothoraGame(address mothoraGameContractAddress) external onlyOwner {
        mothoraGameContract = MothoraGame(mothoraGameContractAddress);
        emit MothoraGameAddressUpdated(mothoraGameContractAddress);
    }

    /**
     * @dev Performs internal operations of staking, including adding the active account to the registry
     */
    function _stakeTokens(uint256 _amount) internal {
        uint256 initialStakedAmount = stakedESSBalance[msg.sender];

        if (initialStakedAmount == 0) {
            if (playerIds[msg.sender] == 0) {
                playerId++;
                playerIds[msg.sender] = playerId;
                playerAddresses.push(msg.sender);
            }
            lastUpdate[msg.sender] = block.timestamp;
        } else {
            stakedDuration[msg.sender] =
                (block.timestamp - lastUpdate[msg.sender]) *
                (initialStakedAmount / stakedESSBalance[msg.sender]); //weighted average of balance & time staked
        }

        stakedESSBalance[msg.sender] += _amount;
        totalStakedBalance += _amount;
    }

    /**
     * @dev Determines boost for the time locked up until now
     */
    function _calculateTimeTier(address _recipient) private returns (uint256) {
        stakedDuration[_recipient] += (block.timestamp - lastUpdate[_recipient]);
        lastUpdate[_recipient] = block.timestamp;
        uint256 stakedDurationLocal = stakedDuration[_recipient];
        if (stakedDurationLocal <= 600) {
            timeTier[_recipient] = 10;
        } else if (stakedDurationLocal > 600 && stakedDurationLocal <= 1200) {
            timeTier[_recipient] = 13;
        } else if (stakedDurationLocal > 1200 && stakedDurationLocal <= 3000) {
            timeTier[_recipient] = 16;
        } else if (stakedDurationLocal > 3000) {
            timeTier[_recipient] = 20;
        }
        return timeTier[_recipient];
    }

    function divider(
        uint256 numerator,
        uint256 denominator,
        uint256 precision
    ) public pure returns (uint256) {
        return ((numerator * (uint256(10)**uint256(precision + 1))) / denominator + 5) / uint256(10);
    }

    function unsafeInc(uint256 x) private pure returns (uint256) {
        unchecked {
            return x + 1;
        }
    }
}
